package com.xiaofan.base;

public class ArrayEquals {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] a1 = new int[]{ 1, 2 };
		int[] a2 = new int[]{ 1, 2 };
		boolean b = a1.equals( a2 );
		System.out.println( b );
	}

}
